import React, { Component } from "react";
import "./App.css";

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,    
      email: null,
      password: null,
      formErrors: {
        firstName: "",        
        email: "",
        password: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();

    if (formValid(this.state)) {
      console.log(`
        --SUBMITTING--
        First Name: ${this.state.firstName}       
        Email: ${this.state.email}
        Password: ${this.state.password}
      `);
    } else {

    }
  };

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };

    switch (name) {
      case "firstName":
        formErrors.firstName =
          value.length < 5 ? "Minimum 5 characters" : "";
        break;

      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "Please enter a valid email address";
        break;
      case "password":
        formErrors.password =
          value.length < 8 ? "Minimum 8 characters" : "";
        break;
      default:
        break;
    }

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };

  render() {
    const { formErrors } = this.state;

    return (


      <div className="createAccountPage">
            <div className="formSection" id="formSection">
               <div className='form-register'>
                   <h1 className='register-headTitle'>Let’s set up your account</h1>
                   <div className='register-subTitle'>
                       Already have a account? 
                       <a href="" className='sign-in'> Sign in
                       </a>
                    </div>



      <div className="wrapper">
        <div className="form-wrapper">

          <form onSubmit={this.handleSubmit} noValidate>
            <div className=" firstName">
              <label htmlFor="firstName" className={formErrors.firstName.length > 0 ? "error" : null}>Your name</label>
              <input
                className={formErrors.firstName.length > 0 ? "error" : null}
                // placeholder="First Name"
                type="text"
                name="firstName"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.firstName}</span>
              )}
            </div>

            <div className="email">
              <label htmlFor="email" className={formErrors.email.length > 0 ? "error" : null}>Email address</label>
              <input
                className={formErrors.email.length > 0 ? "error" : null}
                // placeholder="Email"
                type="email"
                name="email"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.email.length > 0 && (
                <span className="errorMessage">{formErrors.email}</span>
              )}
            </div>

            <div className="firstName describe">          
               <select name="describe" id="selectBox">   
                  <option value="volvo">I would describe my user type as</option>
                  <option value="saab">Developer</option>
                  <option value="opel">Design</option>
               </select>
            </div>        

            <div className="password">
              <label htmlFor="password" className={formErrors.password.length > 0 ? "error" : null}>Password</label>
              <input
                className={formErrors.password.length > 0 ? "error" : null}
                // placeholder="Password"
                type="password"
                name="password"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.password.length > 0 && (
                <span className="errorMessage">{formErrors.password}</span>
              )}
            </div>
            <div className="createAccount">
              <button type="submit">Create Account</button>

            </div>
          </form>
        </div>
      </div>

      <div className='register-subTitle next'>
                   By clicking the “Next” button, you agree to creating a free account, and to  <a href="" className='sign-in'>Terms of Service
                       </a> and <a href="" className='sign-in'>Privacy Policy
                       </a> .
                    </div>

               </div>
            </div>

            <div className="details" id="details">
                <div>
                <h2 className='detail-title'>
                    Dummy Heading
                </h2>
                <p className='details-content'>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                </p>
                </div>            
            </div>
        </div>

    );
  }
}

export default App;
