import React, { Component } from "react";
import "./CreateAccount.css";

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

class CreateAccount extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,    
      email: null,
      password: null,
      describe: null,
      formErrors: {
        firstName: "",        
        email: "",
        password: ""
      },
      isLocked: false
    };
  }

  handleSubmit = e => {
    e.preventDefault();

    // if (formValid(this.state)) {
    //   console.log(`
    //     --SUBMITTING--
    //     First Name: ${this.state.firstName}       
    //     Email: ${this.state.email}
    //     Password: ${this.state.password}
    //   `);
    // } else {
    //   console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
    // }
  };

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };
    switch (name) {
      case "firstName":
        formErrors.firstName =
          value.length < 5 ? "Minimum 5 characters" : "";
        break;

      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "Please enter a valid email address";
        break;
      case "password":
        formErrors.password =
          value.length < 8 ? "Minimum 8 characters" : "";
        break;
      case "describe":
        console.log(formErrors.describe)
        formErrors.describe = 
          value === "none" ? "Select": "";
        break;
      default:
        break;
    }
    if (!this.state.firstName || !this.state.email || !this.state.password || this.state.describe === "none"){

    }else{
      this.setState({isLocked: true});
    };

    this.setState({ formErrors, [name]: value }, () => this.state);
  };

  render() {
    const { formErrors } = this.state;
    const lockedStyle = {
      backgroundColor: "#26607d",
      color: "#ffffff",
    };

    const unlockedStyle = {
        backgroundColor: "#ffffff",
        color: "#26607d",
    };

    return (

      <div className="wrapper">
        <div className="form-wrapper">

          <form onSubmit={this.handleSubmit} noValidate>
            <div className="input_group firstName">

              <input
                className= {formErrors.firstName.length > 0 ? "error" : null}
                // placeholder="First Name"
                type="text"
                name="firstName"
                noValidate
                required
                onChange={this.handleChange}
              />
                <label htmlFor="firstName" className={formErrors.firstName.length > 0 ? "error" : null}>Your name</label>
              {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.firstName}</span>
              )}
            </div>

            <div className="input_group email">

              <input
                className={formErrors.email.length > 0 ? "error" : null}
                // placeholder="Email"
                type="email"
                name="email"
                noValidate
                required
                onChange={this.handleChange}
              />
               <label htmlFor="email" className={formErrors.email.length > 0 ? "error" : null}>Email address</label>
              {formErrors.email.length > 0 && (
                <span className="errorMessage">{formErrors.email}</span>
              )}
            </div>
<div className="firstName describe">          
               <select name="describe" onChange={this.handleChange} id="selectBox">   
                  <option value="none">I would describe my user type as</option>
                  <option value="developer">Developer</option>
                  <option value="design">Design</option>
               </select>
            </div>        

            <div className="input_group password">

              <input
                className={formErrors.password.length > 0 ? "error" : null}
                // placeholder="Password"
                type="password"
                name="password"

                required
                onChange={this.handleChange}
              />
              <label htmlFor="password" className={formErrors.password.length > 0 ? "error" : null}>Password</label>
              {formErrors.password.length > 0 && (
                <span className="errorMessage">{formErrors.password}</span>
              )}
            </div>
            <div className="createAccount">
              {/* {console.log(this.state.isLocked)} */}
              <button style={this.state.isLocked ? lockedStyle : unlockedStyle} type="submit">Create Account</button>

            </div>
          </form>
        </div>
      </div>


    );
  }
}

export default CreateAccount;
